import '../styles/App.scss';
import '../styles/page.scss';
import { useState } from "react";
import initialData from '../data/data.json';


function App() {

  const [data, setData] = useState(initialData);
  const [nameClub , setNameClub] = useState('');
  const [openDaily , setOpenDaily] = useState('');
  const [openWeekend , setOpenWeekend] = useState('');

  const handleSubmit =(ev)=> ev.preventDefault();

  const renderData =()=>{
    return data.map((club, i)=>{
      return(
      <li key={i} className="card"><h3>{`#${i}`}{nameClub}</h3><p>Abierto entre semana: {openDaily}</p><p>Abierto el fin de semana: {openWeekend}</p></li>)
    }
    )
  } 




  return (
    <div className="App">
      <div>
        <h1>Mis clubs</h1>
      <ul className="grid">{renderData()}</ul>
      </div>
      <div>
      <h2>A単adir un nuevo club</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Nombre del club</label>
        <input type="text" name="name" />
        <label htmlFor="daily">多Abre entre semana?</label>
        <input type="checkbox" name="daily" />
        <label htmlFor="weekend">多Abre los fines de semana?</label>
        <input type="checkbox" name="weekend" />
        <input type="button" name="add" value="A単adir un nuevo club" />
      </form>
      </div>
    </div>
  );
}

export default App;
